int main ( ) { int a , t , i , b , c , m , n , g = 1 ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; if ( a <= b ) c = a ; else c = b ; m = a ; n = b ; for ( i = 2 ; i < c ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { a = a / i ; b = b / i ; g = i * g ; i -- ; } if ( a == 1 || b == 1 ) break ; } printf ( "%d %d %d\n" , g , i , ( m * n ) / g ) ; } return 0 ; }