int main ( ) { int i , j ; scanf ( "%d" , & j ) ; for ( i = 0 ; i < j ; i ++ ) { int x , y , m , n ; scanf ( "%d %d" , & x , & y ) ; m = gcd ( x , y ) ; n = lcm ( x , y ) ; printf ( "%d %d\n" , m , n ) ; } return 0 ; } int gcd ( int a , int b ) { if ( b % a == 0 ) return a ; else return gcd ( b % a , a ) ; } int lcm ( int a , int b ) { int k ; k = ( a * b ) / gcd ( a , b ) ; return ( k ) ; }