int main ( ) { int i , j , k , r , suma , sumb , sum1 = 0 , sum2 = 0 ; for ( i = 1 ; i <= 4 ; i ++ ) { suma = 0 ; sumb = 0 ; for ( j = 1 ; j <= i ; j ++ ) { scanf ( "%d" , & k ) ; if ( j == 1 ) { suma = suma + k ; } if ( j == i ) { sumb = sumb + k ; } } sum1 = suma + sum1 ; sum2 = sumb + sum2 ; } if ( sum2 > sum1 ) printf ( " the value of largest sum is%d" , sum2 ) ; else printf ( "\n the value of largest sum is%d" , sum1 ) ; return 0 ; }