int main ( ) { int n , sum1 , sum2 , i , j , k , a , b [ 10000 ] = { 0 } ; scanf ( "%d" , & n ) ; while ( n -- ) { scanf ( "%d" , & a ) ; i = 0 ; a = ( a * ( a + 1 ) ) / 2 ; while ( a -- ) { scanf ( "%d" , & b [ i ] ) ; i ++ ; } sum1 = 0 ; for ( j = 0 ; j < i ; j ++ ) { sum1 = sum1 + b [ j ] ; sum2 = 0 ; for ( k = i - 1 ; k > j ; k -- ) { sum2 = sum2 + b [ k ] ; } if ( sum1 == sum2 ) break ; } printf ( "%d\n" , sum1 ) ; } return 0 ; }