int main ( void ) { long int a , b , hcf , min , temp = 0 , max ; int t , i , j ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%ld%ld" , & a , & b ) ; min = a >= b ? b : a ; max = a >= b ? a : b ; while ( 1 ) { if ( max % min == 0 ) { hcf = min ; break ; } else { temp = min ; min = max % min ; max = temp ; } } printf ( "%\nld" , hcf ) ; printf ( "% ld" , ( ( a * b ) / hcf ) ) ; } return 0 ; }