int min ( int a , int b ) { return ( a < b ) ? a : b ; } int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; int m = min ( a , b ) ; int i , j , k , g , l = 0 ; for ( i = m ; i >= 1 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { g = i ; break ; } } for ( j = 2 ; j <= m ; j ++ ) { if ( a % j == 0 && b % j == 0 ) { l = j ; break ; } } if ( l == 0 ) l = a * b ; printf ( "%d %d\n" , g , l ) ; } }