int main ( ) { int T ; scanf ( "%d" , & T ) ; int i ; for ( i = 0 ; i < T ; i ++ ) { long long int n ; scanf ( "%lld" , & n ) ; long long int arr [ n ] ; int j ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%lld" , & arr [ j ] ) ; } long long int len = 1 ; long long int sum = 0 ; for ( j = 1 ; j < n ; j ++ ) { if ( arr [ j ] >= arr [ j - 1 ] ) { len ++ ; } else { sum += ( ( len * ( len + 1 ) ) / 2 ) ; len = 1 ; } } if ( len > 1 ) { sum += ( ( len * ( len + 1 ) ) / 2 ) ; } if ( n == 1 ) { sum = 1 ; } printf ( "%lld\n" , sum ) ; } return 0 ; }