int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int n , j , k , c = 0 ; scanf ( "%lld" , & n ) ; long long int a [ n ] ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%lld " , & a [ j ] ) ; j = 0 ; while ( n > 1 ) { if ( a [ j ] > a [ j + 1 ] ) { c = c + a [ j + 1 ] ; for ( k = j ; k < n - 1 ; k ++ ) a [ k ] = a [ k + 1 ] ; n -- ; } else { c = c + a [ j ] ; for ( k = j + 1 ; k < n - 1 ; k ++ ) a [ k ] = a [ k + 1 ] ; n -- ; } j ++ ; } printf ( "%lld\n" , c ) ; } return 0 ; }