int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t > 0 && t <= 5 ) { int n , max = 0 , temp = 0 ; scanf ( "%d" , & n ) ; if ( n > 0 && n <= 1000 ) { int ar [ n ] ; int i ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & ar [ i ] ) ; } for ( i = 0 ; i < n - 1 ; i ++ ) { if ( ar [ i + 1 ] - ar [ i ] > 0 ) temp ++ ; else temp = 0 ; if ( temp == 1 ) max += temp ; } printf ( "%d\n" , n + max ) ; } t -- ; } return 0 ; }