int main ( void ) { int t , n ; scanf ( "%d" , & t ) ; for ( ; t > 0 ; t -- ) { scanf ( "%d" , & n ) ; int x [ n ] , z [ n ] , a = 0 , p = 0 , i ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & x [ i ] ) ; z [ i ] = 1 ; } for ( i = 1 ; i < n ; i ++ ) { if ( x [ i ] > x [ i - 1 ] ) z [ p ] ++ ; else p ++ ; } for ( i = 0 ; i < n ; i ++ ) { z [ i ] = ( ( z [ i ] ) * ( z [ i ] - 1 ) ) / 2 ; a = a + z [ i ] ; } printf ( "%d\n" , a + n ) ; } }