int main ( ) { int arr [ 100000 ] , k , i , n , j , t , sum = 0 , max ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { sum = 0 ; scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%d" , & arr [ j ] ) ; sum = n ; for ( j = 0 ; j < n ; j ++ ) { max = arr [ j ] ; for ( k = j + 1 ; k < n ; k ++ ) if ( arr [ k ] > max && k - j == 1 ) { max = arr [ k ] ; sum ++ ; } else break ; } printf ( "%d\n" , sum ) ; } }