int main ( ) { int T ; int ans [ 201 ] ; int sums [ 201 ] ; int i , j ; int Tc ; int n ; int num [ 201 ] ; scanf ( "%d" , & T ) ; Tc = T ; while ( Tc -- ) { memset ( ( void * ) sums , 0 , sizeof ( sums ) ) ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j < ( i + 1 ) ; j ++ ) { scanf ( "%d" , & num [ j ] ) ; } for ( j = i ; j >= 1 ; j -- ) { sums [ j ] = MAX ( sums [ j ] + num [ j ] , sums [ j - 1 ] + num [ j ] ) ; } sums [ 0 ] += num [ 0 ] ; } ans [ Tc ] = 0 ; for ( i = 0 ; i < n ; i ++ ) { ans [ Tc ] = MAX ( sums [ i ] , ans [ Tc ] ) ; } } while ( T -- ) { printf ( "%d\n" , ans [ T ] ) ; } return 0 ; }