int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long int a , b , gcd , lcm , i , min , max ; scanf ( "%ld%ld" , & a , & b ) ; if ( a > b ) { min = b ; max = a ; } else { min = a ; max = b ; } gcd = 1 ; for ( i = 2 ; i <= min ; i ++ ) if ( a % i == 0 && b % i == 0 ) gcd = i ; lcm = ( min * ( max / gcd ) ) ; printf ( "%ld %ld\n" , gcd , lcm ) ; } return 0 ; }