main ( ) { int i , t ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { unsigned int a , b , k = 1 ; scanf ( "%d%d" , & a , & b ) ; int j ; for ( j = 2 ; j <= ( a < b ? a : b ) ; j ++ ) { if ( a % j == 0 && b % j == 0 ) k = j ; } long long int c = ( a * b ) / k ; printf ( "%d %d\n" , k , c ) ; } }