int main ( ) { int g , l , remainder , numerator , denominator , n , i ; long a , b ; scanf ( "%d" , & n ) ; for ( i = 1 ; i <= n ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; if ( a > b ) { numerator = a ; denominator = b ; } else { numerator = b ; denominator = a ; } remainder = numerator % denominator ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } g = denominator ; l = a * b / g ; printf ( "%d %d\n" , g , l ) ; } return 0 ; }