int main ( ) { int n ; scanf ( "%d" , & n ) ; int a , b , gcd [ n ] , lcm [ n ] ; int i , j ; for ( j = 0 ; j < n ; j ++ ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; for ( i = 1 ; i <= a && i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) gcd [ j ] = i ; } lcm [ j ] = ( a * b ) / gcd [ j ] ; } for ( i = 0 ; i < n ; i ++ ) { printf ( "%d %d\n" , gcd [ i ] , lcm [ i ] ) ; } }