int main ( ) { int T , i ; int R , j , k , num , currmaxSum [ 100 ] , nxtmaxSum [ 100 ] , Answer ; scanf ( "%d" , & T ) ; for ( i = 0 ; i < T ; i ++ ) { scanf ( "%d" , & R ) ; Answer = 0 ; for ( j = 0 ; j < R ; j ++ ) { currmaxSum [ j ] = 0 ; for ( k = 0 ; k <= j ; k ++ ) { scanf ( "%d" , & num ) ; if ( k != 0 ) nxtmaxSum [ k ] = currmaxSum [ k ] > currmaxSum [ k - 1 ] ? currmaxSum [ k ] + num : currmaxSum [ k - 1 ] + num ; else nxtmaxSum [ k ] = currmaxSum [ k ] + num ; } for ( k = 0 ; k <= j ; k ++ ) { currmaxSum [ k ] = nxtmaxSum [ k ] ; } } for ( j = 0 ; j < R ; j ++ ) if ( currmaxSum [ j ] > Answer ) Answer = currmaxSum [ j ] ; printf ( "%d\n" , Answer ) ; } return 0 ; }