int main ( ) { int t , i , a , b , small , large , gcd , lcm , j ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; if ( a < b ) { small = a ; large = b ; } else { small = b ; large = a ; } for ( j = small ; j <= b * a ; j ++ ) { if ( j % a == 0 && j % b == 0 ) { lcm = j ; break ; } } for ( j = a * b ; j >= 1 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { gcd = j ; break ; } } printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }