main ( ) { int t , i , k ; long a , b , min , max ; scanf ( "%d" , & t ) ; int hcf [ t ] , lcm [ t ] ; for ( i = 0 ; i < t ; i ++ ) { printf ( "\n" ) ; scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; hcf [ i ] = 1 ; if ( a > b ) { min = b ; max = a ; } else { min = a ; max = b ; } if ( max % min == 0 ) { hcf [ i ] = min ; lcm [ i ] = max ; } else { for ( k = 2 ; k <= min / 2 ; k ++ ) { if ( min % k == 0 && max % k == 0 ) hcf [ i ] = k ; } lcm [ i ] = ( max * min ) / hcf [ i ] ; } } for ( i = 0 ; i < t ; i ++ ) { printf ( "\n" ) ; printf ( "%d %d" , hcf [ i ] , lcm [ i ] ) ; } }