main ( ) { int t , i , g , sm , bg ; scanf ( "%d" , & t ) ; for ( g = 1 ; g <= t ; g ++ ) { int a , b , hcf , lcm ; scanf ( "%d%d" , & a , & b ) ; if ( a > b ) { sm = b ; bg = a ; } else { sm = a ; bg = b ; } for ( i = 1 ; i <= sm ; i ++ ) { if ( a % i == 0 && b % i == 0 ) hcf = i ; } for ( i = bg ; i <= a * b ; i ++ ) { if ( i % a == 0 && i % b == 0 ) { lcm = i ; break ; } } printf ( "%d  %d" , hcf , lcm ) ; } }