int main ( ) { int tc ; scanf ( "%d" , & tc ) ; int n ; for ( int i = 0 ; i < tc ; i ++ ) { scanf ( "%d" , & n ) ; int a [ n ] [ n ] ; for ( int j = 0 ; j < n ; j ++ ) for ( int k = 0 ; k <= i ; k ++ ) scanf ( "%d" , & a [ j ] [ k ] ) ; int M = 0 , sum ; for ( int i = 0 ; i < n - 1 ; i ++ ) { int j = 0 , sum = 0 ; sum = a [ i ] [ j ] + a [ i + 1 ] [ j ] + a [ i + 1 ] [ j + 1 ] ; if ( sum > M ) M = sum ; } printf ( "%d\n" , M ) ; } return 0 ; }