int MAX ( int a , int b ) { return a > b ? a : b ; } int MIN ( int a , int b ) { return a < b ? a : b ; } int LCM ( int a , int b ) { int lcm ; for ( int i = 1 ; ; i ++ ) { if ( MAX ( a , b ) * i % MIN ( a , b ) == 0 ) { lcm = MAX ( a , b ) * i ; break ; } } return lcm ; } int HCF ( int a , int b ) { int hcf ; for ( int i = MIN ( a , b ) ; i > 0 ; i -- ) { if ( MAX ( a , b ) % i == 0 && MIN ( a , b ) % i == 0 ) { hcf = i ; break ; } } return hcf ; } int main ( ) { int n ; scanf ( "%d" , & n ) ; for ( int i = 1 ; i <= n ; i ++ ) { int a , b ; scanf ( "\n%d %d" , & a , & b ) ; printf ( "%d %d" , HCF ( a , b ) , LCM ( a , b ) ) ; } return 0 ; }