int gcd ( int a , int b ) { int r = a % b ; if ( r == 0 ) return b ; else return gcd ( b , r ) ; } int lcm ( int a , int b ) { long int r = ( a * b ) / gcd ( a , b ) ; return r ; } int main ( void ) { int t , a , b ; scanf ( "%d" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; int g = gcd ( a , b ) ; long int l = lcm ( a , b ) ; printf ( "%d" , g ) ; printf ( "%  ld\n" , l ) ; } return 0 ; }