int main ( void ) { int t ; long long int i , min = 0 , cost = 0 , n ; long long int a [ 50000 ] ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%lli" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lli" , & a [ i ] ) ; } if ( a [ 0 ] < a [ 1 ] ) min = a [ 0 ] ; else min = a [ 1 ] ; cost = min ; for ( i = 2 ; i < n ; i ++ ) { if ( min < a [ i ] ) { cost = cost + min ; } else { min = a [ i ] ; cost = cost + min ; } } printf ( "%lli" , cost ) ; printf ( "\n" ) ; } return 0 ; }