int gcd ( long int a , long int b ) { while ( a != b ) { if ( a > b ) { a = a - b ; } else { b = b - a ; } } return a ; } int main ( ) { int t ; long int a , b ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; int g = gcd ( a , b ) ; int l = ( a * b ) / g ; printf ( "%d %d\n" , g , l ) ; } }