int main ( ) { long int i , j , big , m , t , n , p ; long int arr [ 100 ] , sum [ 1000 ] , k [ 1000 ] ; scanf ( "%ld" , & t ) ; while ( t != 0 ) { scanf ( "%ld" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { sum [ i ] = 0 ; k [ i ] = 0 ; } for ( i = 0 ; i < n ; i ++ ) { sum [ i ] = 0 ; m = 1 ; for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%ld" , & arr [ j ] ) ; if ( j == 0 ) sum [ 0 ] = k [ 0 ] + arr [ 0 ] ; else if ( j == i ) sum [ m ++ ] = arr [ j ] + k [ j - 1 ] ; else for ( p = j - 1 ; p <= ( i + j - 1 ) ; p ++ ) sum [ m ++ ] = k [ p ] + arr [ j ] ; } for ( j = 0 ; j < m ; j ++ ) k [ j ] = sum [ j ] ; } big = 0 ; for ( i = 0 ; i < m ; i ++ ) if ( big < sum [ i ] ) big = sum [ i ] ; printf ( "%ld" , big ) ; t -- ; } return 0 ; }