int gcd ( int m , int n ) { if ( n == 0 ) return m ; else return gcd ( n , m % n ) ; } int lcm ( int m , int n ) { int max ; if ( m > n ) max = m ; else max = n ; while ( 1 ) { if ( max % m == 0 && max % n == 0 ) { return max ; break ; } ++ max ; } } int main ( ) { long t , a , b , g , l ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%ld%ld" , & a , & b ) ; g = gcd ( a , b ) ; l = lcm ( a , b ) ; printf ( "%d %d\n" , g , l ) ; } }