int main ( void ) { int t , j ; scanf ( "%d" , & t ) ; for ( j = 0 ; j < t ; j ++ ) { long int a , b , i , hcf = 0 ; scanf ( "%ld %ld" , & a , & b ) ; i = b ; i = ( a > b ? b : a ) ; for ( ; i > 0 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { hcf = i ; break ; } } printf ( "%ld " , hcf ) ; if ( hcf == 0 ) printf ( "0\n" ) ; else printf ( "%ld\n" , ( a * ( b / hcf ) ) ) ; } return 0 ; }