int main ( ) { int i , n , j , sum = 0 , t ; int a [ 100 ] ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d" , & n ) ; for ( j = 1 ; j <= n ; j ++ ) { scanf ( "%d" , & a [ j ] ) ; } j = 1 ; while ( j != ( n ) ) { if ( a [ j ] > a [ j + 1 ] ) { a [ j ] = a [ j ] ; sum = sum + a [ j + 1 ] ; } else { a [ j + 1 ] = a [ j ] ; sum = sum + a [ j + 1 ] ; } j ++ ; } printf ( "%d" , sum ) ; sum = 0 ; } return 0 ; }