int main ( ) { int i , j , t , s = 0 , n ; scanf ( "%d\n" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { s = 0 ; scanf ( "%d\n" , & n ) ; int a [ n ] , b [ n - 1 ] ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%d" , & a [ j ] ) ; } for ( j = 0 ; j < n - 1 ; j ++ ) { if ( a [ j ] < a [ j + 1 ] ) b [ j ] = a [ j ] ; else b [ j ] = a [ j + 1 ] ; s += b [ j ] ; } printf ( "%d\n" , s ) ; } return 0 ; }