long long int gcd ( long int a , long int b ) { if ( b == 0 ) return a ; else return gcd ( b , a % b ) ; } int main ( ) { long int t , a , b ; long long int l , g ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; g = gcd ( a , b ) ; l = ( a * b ) / g ; printf ( "%d %d\n" , g , l ) ; } }