int main ( ) { int t ; int d , f ; int i , j , l ; int k ; int n ; int rows ; scanf ( "%d" , & t ) ; int sum [ t ] ; for ( i = 0 ; i < t ; i ++ ) { sum [ i ] = 0 ; k = 0 ; scanf ( "%d" , & rows ) ; n = ( rows * ( rows + 1 ) ) / 2 ; int a [ n ] ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%d" , & a [ j ] ) ; } for ( l = 0 ; l < rows ; l ++ ) { sum [ i ] = sum [ i ] + a [ k ] ; d = a [ k + l + 1 ] - a [ k + l + 2 ] ; if ( k + 2 * l + 3 < n ) { f = a [ k + ( 2 * l ) + 3 ] - a [ k + l + 2 ] ; } else f = 0 ; if ( d > 0 || ( f > 0 ) ) { k = k + l + 1 ; } else if ( d <= 0 || ( f < 0 ) ) { k = k + l + 2 ; } } } for ( i = 0 ; i < t ; i ++ ) { printf ( "%d\n" , sum [ i ] ) ; } return 0 ; }