long long int gcd ( long long int a , long long int b ) { if ( a > b ) { int temp = a ; a = b ; b = temp ; } while ( b % a != 0 ) { int t = a ; a = b % a ; b = t ; } return a ; } int main ( ) { int i , ntest ; scanf ( "%d" , & ntest ) ; for ( i = 0 ; i < ntest ; i ++ ) { long long int a , b , lcm , hcf ; scanf ( "%lld%lld" , & a , & b ) ; hcf = gcd ( a , b ) ; lcm = ( a * b ) / hcf ; printf ( "%lld %lld\n" , hcf , lcm ) ; } return 0 ; }