void main ( ) { int a , b , t , rem , nr , dr , gcd , lcm ; scanf ( "%ld" , & t ) ; while ( t -- ) { scanf ( "%ld%ld" , & a , & b ) ; if ( a > b ) { nr = a ; dr = b ; } else nr = b ; dr = a ; while ( dr != 0 ) { rem = nr % dr ; nr = dr ; rem = dr ; } gcd = nr ; lcm = ( a * b ) / gcd ; printf ( "%ld\n" , gcd ) ; printf ( "%ld\n" , lcm ) ; } }