int GCD ( int a , int b ) { return ( a == b ) ? a : ( a > b ) ? GCD ( a - b , b ) : GCD ( a , b - a ) ; } int LCM ( int a , int b ) { int temp = ( a > b ) ? a : b ; while ( ! ( temp % a == 0 && temp % b == 0 ) ) temp ++ ; return temp ; } int main ( ) { int * list , i , count , * list2 ; int a , b ; scanf ( "%d" , & count ) ; list = malloc ( count * sizeof ( int ) ) ; list2 = malloc ( count * sizeof ( int ) ) ; for ( i = 0 ; i < count ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; list [ i ] = GCD ( a , b ) ; list2 [ i ] = LCM ( a , b ) ; } for ( i = 0 ; i < count ; i ++ ) { printf ( "%d %d\n" , list [ i ] , list2 [ i ] ) ; } return 0 ; }