int main ( ) { long int n , i , j ; scanf ( "%ld" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { long int sum = 0 ; long int m = 0 ; long int t ; scanf ( "%ld" , & t ) ; int * a = ( int * ) malloc ( sizeof ( int ) * ( ( t * ( t + 1 ) ) / 2 ) ) ; for ( j = 0 ; j < t ; j ++ ) { long int k = j + 1 ; while ( k != 0 ) { scanf ( "%d" , & a [ m ] ) ; m ++ ; k -- ; } } long int index = 0 ; sum = sum + a [ index ] ; index = 1 ; j = 1 ; long int l = 0 ; while ( ( j != t ) ) { if ( a [ l + j ] > a [ l + j + 1 ] ) { sum = sum + a [ l + j ] ; l = l + j ; j ++ ; } else { sum = sum + a [ l + j + 1 ] ; l = l + j + 1 ; j ++ ; } } free ( a ) ; printf ( "%ld\n" , sum ) ; } return 0 ; }