int main ( ) { int t ; scanf ( "%d" , & t ) ; for ( int i = 1 ; i <= t ; i ++ ) { long a , b ; scanf ( "%ld %ld" , & a , & b ) ; long g , p , hcf , lcm ; p = a * b ; if ( a > b ) { g = b ; } else { g = a ; } if ( g % ( p / g ) == 0 ) { hcf = p / g ; } else { for ( long j = g ; j >= 1 ; j -- ) { if ( a % j == 0 && b % j == 0 ) { hcf = j ; break ; } } } lcm = p / hcf ; printf ( "%ld %ld\n" , hcf , lcm ) ; } return 0 ; }