int main ( ) { int A , B , T , C , D , remainder , gcd , lcm , i ; scanf ( "%d" , & T ) ; for ( i = 0 ; i < T ; i ++ ) { scanf ( "%d" , & A ) ; scanf ( "%d" , & B ) ; if ( A > B ) { C = A ; D = B ; } else { C = B ; D = A ; } remainder = C % D ; while ( remainder != 0 ) { C = D ; D = remainder ; remainder = C % D ; } gcd = D ; lcm = A * B / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }