int main ( ) { int t ; scanf ( "%d" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { int n ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( int j = 0 ; j < n ; j ++ ) { scanf ( "%d " , & a [ j ] ) ; } int min = 100001 ; for ( int j = 0 ; j < n ; j ++ ) { if ( a [ j ] < min ) min = a [ j ] ; } int cost = min * ( n - 1 ) ; printf ( "%d\n" , cost ) ; } return 0 ; }