int gcd ( long long int , long long int ) ; int main ( ) { int t , i ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { long long int a , b , result ; scanf ( "%lld%lld" , & a , & b ) ; result = gcd ( a , b ) ; printf ( "%lld %lld\n" , result , ( a * b ) / ( result ) ) ; } } int gcd ( long long int a , long long int b ) { if ( a == 0 || b == 0 ) return 0 ; else { while ( a != b ) { if ( a > b ) { return gcd ( a - b , b ) ; } else { return gcd ( a , b - a ) ; } } return a ; } }