int main ( ) { int t , a , b , g , l , i ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; if ( a > b ) { { for ( i = b ; i >= 1 ; i -- ) if ( a % i == 0 && b % i == 0 ) { g = i ; break ; } else continue ; } { if ( a % b == 0 ) l = a ; else l = a * b ; } } else if ( b > a ) { { for ( i = a ; i >= 1 ; i -- ) if ( a % i == 0 && b % i == 0 ) { g = i ; break ; } else continue ; } { if ( b % a == 0 ) l = b ; else l = a * b ; } } else g = l = a ; printf ( "%d %d\n" , g , l ) ; } return 0 ; }