unsigned long int gcd ( unsigned long int a , unsigned long int b ) { if ( b == 0 ) return a ; else { return gcd ( b , a % b ) ; } } unsigned long int lcm ( unsigned long int a , unsigned long int b ) { unsigned long int i ; for ( i = a ; i <= a * b ; i ++ ) { if ( i % a == 0 && i % b == 0 ) break ; } return i ; } int main ( ) { int t ; unsigned long int a , b , g , l ; scanf ( "%d" , & t ) ; while ( t > 0 ) { scanf ( "%lu%lu" , & a , & b ) ; if ( a > b ) { g = gcd ( a , b ) ; l = lcm ( a , b ) ; } else { g = gcd ( b , a ) ; l = lcm ( a , b ) ; } printf ( "%lu %lu\n" , g , l ) ; t -- ; } return 0 ; }