int main ( ) { long long int a [ 100000 ] ; long long int i , j , k , min , t ; long long int n ; scanf ( "%lld" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld" , & n ) ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%lld" , & a [ j ] ) ; } min = a [ 0 ] ; for ( k = 0 ; k < n ; k ++ ) { if ( a [ k ] < min ) min = a [ k ] ; } printf ( "%lld \n" , min * ( n - 1 ) ) ; } return 0 ; }