int greatestCommonInteger ( int a , int b ) { if ( a == 0 ) { return b ; } else { return greatestCommonInteger ( b % a , a ) ; } } int main ( void ) { int a , b ; int n ; int lcm , gcd ; scanf ( "%d" , & n ) ; int i ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; gcd = greatestCommonInteger ( a , b ) ; lcm = ( a * b ) / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }