int main ( ) { int t , i , n , j , k , m , end ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; m = n * ( n + 1 ) / 2 ; int a [ m ] ; m -- ; end = m ; for ( j = 0 ; j <= m ; j ++ ) scanf ( "%d" , & a [ j ] ) ; while ( n > 1 ) { m -= n ; n -- ; for ( k = 1 ; k <= n ; k ++ ) a [ m - k + 1 ] += a [ end - k ] > a [ end - k + 1 ] ? a [ end - k ] : a [ end - k + 1 ] ; end -= n + 1 ; } printf ( "%d\n" , a [ 0 ] ) ; } return 0 ; }