int gcd_calc ( int * a , int * b ) { if ( * a < * b ) { int temp = * a ; * a = * b ; * b = temp ; } int rem , n1 , n2 ; n1 = * a ; n2 = * b ; while ( n2 != 0 ) { rem = n1 % n2 ; n1 = n2 ; n2 = rem ; } return n1 ; } int main ( ) { int t , a , b , gcd , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; gcd = gcd_calc ( & a , & b ) ; lcm = a / gcd ; lcm = lcm * b ; printf ( "\n%d %d" , gcd , lcm ) ; } return 0 ; }