int main ( ) { int i , n , a , b , j , gcd , lcm , max ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; if ( a > b ) { for ( j = b ; j >= 1 ; j -- ) { if ( ( a % j == 0 ) && ( b % j == 0 ) ) { gcd = j ; break ; } } max = a ; } else { for ( j = a ; j >= 1 ; j -- ) { if ( ( a % j == 0 ) && ( b % j == 0 ) ) { gcd = j ; break ; } } max = b ; } while ( 1 ) { if ( ( max % a == 0 ) && ( max % b == 0 ) ) { lcm = max ; break ; } max ++ ; } printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }