int main ( ) { int a [ 10 ] , n , i , t , sum , temp ; scanf ( "%d\n" , & t ) ; while ( t != 0 ) { sum = 0 ; scanf ( "%d\n" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) sum = sum + a [ i + 1 ] ; else { sum = sum + a [ i ] ; temp = a [ i ] ; a [ i ] = a [ i + 1 ] ; a [ i + 1 ] = temp ; } } t -- ; } return 0 ; }