int main ( ) { int n , t , i , b , count = 2 , sum = 0 , great = 0 ; scanf ( "%d" , & b ) ; for ( ; b > 0 ; b -- ) { scanf ( "%d" , & n ) ; t = ( ( n ) * ( n + 1 ) ) / 2 ; int a [ t ] ; for ( i = 1 ; i < t ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } sum = a [ 1 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ count + great ] >= a [ count + great + 1 ] ) { sum = sum + a [ count + great ] ; count = count + great + 1 ; } else { sum = sum + a [ count + great + 1 ] ; count = count + great + 2 ; } great = great + 1 ; } printf ( "%d\n" , sum ) ; } return 0 ; }