int main ( ) { int t , i , j , temp , n ; scanf ( "%d" , & t ) ; while ( t -- ) { long int n ; scanf ( "%ld" , & n ) ; long long int arr [ n ] ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%lld" , & arr [ j ] ) ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j < n ; j ++ ) { if ( arr [ j ] > arr [ j + 1 ] ) { temp = arr [ j + 1 ] ; arr [ j + 1 ] = arr [ j ] ; arr [ j ] = temp ; } } } printf ( "%d\n" , arr [ 0 ] * ( n - 1 ) ) ; } return 0 ; }